var combiningDiacritics = /[\u0300-\u036F\uFE20-\uFE2F\u20D0-\u20FF\u1DC0-\u1DFF\u1AB0-\u1AFF\u0300-\u036F\u02B0-\u02FF]/gi
var nonAscii = /[^\x00-\x7F]/g

exports.normalizeAndDiacritics = function(str){

	if (typeof str != "string") return ""

	str = str.replace(combiningDiacritics,"")
	
	str = removeDiacritics(str.toLowerCase().replace(/[.,-\/#!$%\^&\*;:{}=ʻ̲̣_’\+\\\|@`~<>()\[\]\"\-\'\?]/g,' ')).replace(/\sand\s/,' ').replace(/\s\s+/g, ' ').trim().latinize()

	str = str.replace(nonAscii,"").trim()

	return str

}

exports.singularize = function(str){

	var words = str.split(" ")

	for (var x in words){
		words[x] = pluralize.singular(words[x])
	}

	return words.join(" ")


}

exports.percentOverlap = function(source,compare){


	if (source.split(" ").length < 3 || compare.split(" ").length < 3) return 0;


	var baseLine = overlap(source, source, {
		ignoreCase: true,
		minWordLength: 2,
		ignoreCommonWords: true,
		depluralize: true,
	}).length

	var results = overlap(source, compare, {
		ignoreCase: true,
		minWordLength: 2,
		ignoreCommonWords: true,
		depluralize: true,
	}).length



	return results/baseLine * 100




}



exports.removePunctAndKeyWords = function(s){

	if (!s) s = ""

	//take out punctuation and extra whitespace
	s = s.replace(/[\.,-\/#!$%\^&\*;:{}=\-_`~()]/g," ").replace(/\s+/g, " ").trim()

	for (var x in removeFromTitles){
		s = s.replace(removeFromTitles[x],'').trim()
	}
	return s
}


//passes two strings will find the similarity and return float if fuzzy then use that 0.0-1
//take out extra whitespace, punctuation 
exports.compareNormalizedTitles = function(a,b,fuzzy){
	if (!a || !b) return false

	a = exports.removePunctAndKeyWords(a)
	b = exports.removePunctAndKeyWords(b)

	if (a.toString().length >= b.toString().length){
		if (fuzzy){
			return a.toString().score(b,fuzzy)
		}else{
			return a.toString().score(b)
		}
	}else{
		if (fuzzy){
			return b.toString().score(a,fuzzy)
		}else{
			return b.toString().score(a)
		}
	}
}


//passes two strings will find the similarity and return float if fuzzy then use that 0.0-1
exports.compareTitles = function(a,b,fuzzy){
	if (!a || !b) return false

	if (a.toString().length >= b.toString().length){
		if (fuzzy){
			return a.toString().score(b,fuzzy)
		}else{
			return a.toString().score(b)
		}
	}else{
		if (fuzzy){
			return b.toString().score(a,fuzzy)
		}else{
			return b.toString().score(a)
		}
	}
}

exports.normalize = function(input){

	if (!input) return false

	//force to string
	input = input.toString()

	input = input.toLowerCase()

	input = S(input).unescapeHTML().s

	input = input.replace(/\s/g, "")


	return input

}

//pass a object full of identifers and it will return the values normalized
exports.normalizeIdents = function(input){

	if (!input) return false

	var results = {}

	for (var x in input){

		results[x] = exports.normalize(input[x])


	}

	return results

}


//pass a string and it will make sure it is in bnumber format
exports.normalizeBnumber = function(bnumber){

	if (!bnumber) return false

	bnumber = bnumber.toString()

	//take off the b for now
	bnumber = bnumber.replace("b",'')

	bnumber = bnumber.replace(".",'')

	//split off any check sum thing
	bnumber = bnumber.split("~")[0]

	bnumber = bnumber.replace(/\s/g,'')

	//if it is long (why?) cut off the extra digit
	if (bnumber.length > 8){
		bnumber = bnumber.substring(0,8)
	}


	//make sure this a number
	if ( isNaN(bnumber) ){
		return false
	}else{
		return "b" + bnumber
	}

	return results

}




